CREATE TABLE IF NOT EXISTS `obsidian_operations` (
	`id` int AUTO_INCREMENT NOT NULL,
	`operacao` varchar(100) NOT NULL,
	`caminho_arquivo` varchar(1000),
	`status` enum('sucesso','falha','pendente') NOT NULL DEFAULT 'pendente',
	`tentativas` int DEFAULT 1,
	`erro` text,
	`detalhes` text,
	`createdAt` timestamp NOT NULL DEFAULT (now()),
	CONSTRAINT `obsidian_operations_id` PRIMARY KEY(`id`)
);
CREATE INDEX IF NOT EXISTS `operacao_idx` ON `obsidian_operations` (`operacao`);
CREATE INDEX IF NOT EXISTS `status_idx` ON `obsidian_operations` (`status`);
CREATE INDEX IF NOT EXISTS `created_at_idx` ON `obsidian_operations` (`createdAt`);

